mapper.select:
SELECT E.ELEC_APPLY_CODE, E.MEMBER_ID, E.APPLY_DT , E.MEMBER_ID, E.PARKING_NO, P.PARKING_NAME , E.CAR_NO, E.ELECT_DATE , C1.CODE_NAME1 AS CONFIRM_GUBN , C2.CODE_NAME1 AS CANCEL_GUBN , UI.USER_NAME AS CONFIRM_NAME , E.CONFIRM_ID, E.CONFIRM_DT FROM T_ELEC_REDUCTION E INNER JOIN T_PARKING_INFO P ON E.PARKING_NO = P.PARKING_NO LEFT OUTER JOIN T_SYS_COMCODE C1 ON E.CONFIRM_GUBN_PARENT = C1.PARENT_CODE_ID AND E.CONFIRM_GUBN = C1.CODE_ID LEFT OUTER JOIN T_SYS_COMCODE C2 ON E.CANCEL_GUBN_PARENT = C2.PARENT_CODE_ID AND E.CANCEL_GUBN = C2.CODE_ID LEFT OUTER JOIN T_USER_INFO UI ON E.CONFIRM_ID = UI.USER_ID WHERE 1=1
AND DATE_FORMAT(E.APPLY_DT, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND P.P_GOV_CODE = #{kParkingGovCode}
AND E.PARKING_NO = #{kParkingNo}
AND E.CAR_NO LIKE '%${kCarNumber}%'
AND E.CONFIRM_GUBN = #{kConfirmGubn}
AND E.CANCEL_GUBN = #{kCancelGubn}
ORDER BY E.APPLY_DT DESC

mapper.update:
UPDATE T_ELEC_REDUCTION
   SET CONFIRM_GUBN = #{confirmGubn}
	 , CONFIRM_ID = #{userId}
	 , CONFIRM_DT = NOW()

	 , REJECT_REASON = #{rejectReason}

 WHERE ELEC_APPLY_CODE = #{hElecApplyCode}
   AND CAR_NO = #{hCarNo}
   AND MEMBER_ID = #{memberId}
