mapper.insert:
INSERT INTO T_SYS_BTN_INFO (
	BTN_ID, BTN_DISP_TYPE_PARENT, BTN_DISP_TYPE, BTN_CAPTION, BTN_CLASS
  , BTN_ICON, ICON_LOC_PARENT, ICON_LOC, LANG_CODE, USE_YN_PARENT
  , USE_YN, BTN_ORDER, REG_ID, REG_DT, MOD_ID
  , MOD_DT
) VALUES (
	#{grdBtnId}, 'BTN_DISP_TYPE', #{grdBtnDispType}, #{grdBtnCaption}, #{grdBtnClass}
  , #{grdBtnIcon}, 'BTN_ICON_LOC', #{grdIconLoc}, NULL, 'USE_YN'
  , #{grdUseYn}, NULL, #{userId}, NOW(), #{userId}
  , NOW()
)

mapper.update:
UPDATE T_SYS_BTN_INFO
   SET BTN_DISP_TYPE = #{grdBtnDispType}
     , BTN_CAPTION = #{grdBtnCaption}
     , BTN_CLASS = #{grdBtnClass}
     , BTN_ICON = #{grdBtnIcon}
     , ICON_LOC = #{grdIconLoc}
     , USE_YN = #{grdUseYn}
     , MOD_ID = #{userId}
     , MOD_DT = NOW()
 WHERE BTN_ID = #{grdBtnId}

UPDATE T_SYS_BTN_INFO
   SET USE_YN = 'N'
     , MOD_ID = #{userId}
     , MOD_DT = NOW()
 WHERE BTN_ID = #{grdBtnId}

		UPDATE T_SYS_BTN_INFO
           SET BTN_ORDER = #{index} + 1
         WHERE BTN_ID = #{grdBtnId}
