mapper.update:
UPDATE T_USER_INFO
   SET USER_ID = #{grdUserId}
     , POS_GUBN = #{grdPosGubn}

	 , USER_PW = #{grdUserPw}

     , USER_NAME = #{grdUserName}
     , USER_EMAIL = #{grdUserEmail}
     , USER_PHONE = #{grdUserPhone}
     , USE_YN = #{grdUseYn}
     , MOD_ID = #{userId}
     , MOD_DT = NOW()
     , RECV_YN = #{grdRecvYn}
 WHERE USER_ID = #{grdUserId}

UPDATE T_USER_INFO
   SET USE_YN = 'N'
     , MOD_ID = #{userId}
     , MOD_DT = NOW()
 WHERE USER_ID = #{grdUserId}
